import React, { useState, Component } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import ListGroup from "react-bootstrap/ListGroup";
import Button from "react-bootstrap/Button";
import Link from 'next/link';
import Image from 'react-bootstrap/Image';

const layoutStyle = {
    margin: 0,
    padding: 45,
  };

const Footer = () => (
  <div  className="footerDiv" style={layoutStyle} fluid>
    <Container>
        <Row>
            <Col md={2}>
                <Link href="/"><a><Image src="/uniacco-logo-white-s.png" fluid className="mt-1 mb-4" /></a></Link>
                <ListGroup variant="flush" className="disclaimer mb-5">
                <span>Adventum Student Living Pvt. Ltd.</span>
                <span>A-4301, 43rd Floor, Lodha Bellisimo, N.M.Joshi Marg, Mahalakshmi Mumbai- 400011</span>
                <span>The content, images and logos used on this are copyright protected and copyrights vests with the respective owners. The usage is intended to promote, identify and search. No endorsement or partnership is implied.</span>
                </ListGroup>
            </Col>
            <Col md={3} className="pl-md-5">
                <Link href="/about-us"><h6><a>about us</a></h6></Link>                
                <ListGroup variant="flush" className="py-2">
                    <a href="https://uniacco.com/blog/" target="_blank"><ListGroup.Item>blog</ListGroup.Item></a>
                    <Link href="/help/faqs"><a><ListGroup.Item>faqs</ListGroup.Item></a></Link>
                    <Link href="/how-it-works"><a><ListGroup.Item>how it works</ListGroup.Item></a></Link>
                    <Link href="/refer"><a><ListGroup.Item>refer</ListGroup.Item></a></Link>
                    <a href="https://uniacco.com/careers/" target="_blank"><ListGroup.Item>careers</ListGroup.Item></a>
                    <Link href="/terms"><a><ListGroup.Item>terms</ListGroup.Item></a></Link>
                    <Link href="/privacy-policy"><a><ListGroup.Item>privacy policy</ListGroup.Item></a></Link>
                </ListGroup>
            </Col>
            <Col md={3} className="pl-md-5 address">
                <Link href="/contact-us"><h6><a>contact us</a></h6></Link>
                <ListGroup variant="flush">
                    <ListGroup.Item><small><span className="text-gray">UK</span>
                        <br />25 Upper Brook Street,Mayfair, London W1K 7QD</small>
                    </ListGroup.Item>
                    <ListGroup.Item><small><span className="text-gray">India</span>
                        <br />A-2403, Adventum, Marathon Futurex, Lower Parel, Mumbai, Maharashtra 400013</small>
                    </ListGroup.Item>
                </ListGroup>
            </Col>
            <Col className="pr-0">
                <h6 className="pl-md-4 ml-md-2">sign up to our newsletter</h6>
                <h6 className="pl-md-4 ml-md-2">connect with us</h6>
            </Col>
            <style>{`
            button {
                background-color: #F05B4E;
            }            
            div.card {
                margin-top: 1em;
            }
            .footerDiv {
                background-color: #101A42;
                padding-top: 3em;
                padding-bottom: 3em;

            }
            .hr {
                margin: 2em 2em 2em 0em;
                border: 1px solid gray;
                background-color: gray; 
            }
            `}</style>
        </Row>
        <Row className="footerCopy">
            <Col md={{ span: 12, offset: 0 }}>
                <p className="text-white">&copy; 2019 All Rights Reserved</p>
            </Col>
        </Row>
        <style jsx>{`
        h6 {
            font-family: 'Clarika Grotesque';
            font-size: 1.4em;
            font-weight: 600;
            line-height: 1.7em;
            color: #ffffff;
        }
        a:hover {
            cursor: pointer;
            color: #F05B4E !important;
        }
        `}</style>
    </Container>
  </div>
);

export default Footer;
